function setupToggleArea(e){var n=e.find("header"),a=e.find("section"),o=n.find("> button"),d=n.find("> a");n.find("[data-elm]").on("click",function(e){e.preventDefault(),a.hasClass("hidden")?d.find("i").removeClass("fa-chevron-down").addClass("fa-chevron-up"):d.find("i").removeClass("fa-chevron-up").addClass("fa-chevron-down"),a.toggleClass("hidden"),o.toggleClass("active")})}$(function(){$(".toggle-area").each(function(e,n){setupToggleArea($(n))})});